import React from "react";
import { motion } from "framer-motion";

/*
  App.jsx - Main animated love story app.
  Replace texts / images / music in the components or sections below.
*/

export default function App() {
  return (
    <div className="min-h-screen bg-gradient-to-b from-pink-100 via-rose-200 to-pink-300 text-gray-900 font-sans overflow-x-hidden">
      {/* Floating Hearts Background */}
      <div className="pointer-events-none fixed inset-0 overflow-hidden">
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            initial={{ opacity: 0, y: 0 }}
            animate={{ opacity: [0, 1, 1, 0], y: [-20, -200 - i * 10] }}
            transition={{ duration: 8 + i, repeat: Infinity, delay: i * 0.5 }}
            className="absolute text-rose-400 text-3xl"
            style={{ left: `${Math.random() * 100}%`, top: `${Math.random() * 100}%` }}
          >
            ❤️
          </motion.div>
        ))}
      </div>

      {/* Hero Section */}
      <section className="h-screen flex flex-col items-center justify-center text-center px-6 relative z-10">
        <motion.h1
          initial={{ opacity: 0, scale: 0.7 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1 }}
          className="text-6xl md:text-8xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-rose-700 to-red-500 drop-shadow-lg"
        >
          Our Beautiful Journey
        </motion.h1>

        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5, duration: 1 }}
          className="text-xl md:text-3xl max-w-2xl mt-4 font-light"
        >
          A love story filled with magic, moments, and memories.
        </motion.p>

        <motion.div
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 1, duration: 1 }}
          className="mt-12"
        >
          <a
            href="#story"
            className="bg-gradient-to-r from-rose-600 to-red-500 text-white px-10 py-4 rounded-full text-xl shadow-2xl hover:scale-110 transition-transform duration-300"
          >
            Start Our Story
          </a>
        </motion.div>
      </section>

      {/* Story Timeline */}
      <section id="story" className="py-24 px-6 bg-white text-gray-900 relative z-10">
        <div className="max-w-4xl mx-auto">
          <motion.h2
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 1 }}
            className="text-5xl font-bold text-center text-rose-700 mb-16"
          >
            Our Timeline
          </motion.h2>

          <div className="relative border-l-4 border-rose-400 ml-6 space-y-16">
            {[
              {
                title: "✨ Kahani Do Dilon Ki ✨",
                text: " Kahani do dilo ki… ,  Aaj date hai 22 November 2025. , Kuch saal pehle do bilkul ajnabi log mile the… , Pehle baatein bilkul normal thi, phir dheere-dheere dosti hui… , Ek din achanak ladki ko ek message aaya — , “I love you.” , Ye ladki ke liye sochne wali baat thi…  , Jo ladka apni Instagram ID nahi dena chahata tha, , wo aaj itni badi baat kaise bol raha tha? , Isliye usne use mazaak samajh kar ignore kar diya. , Lekin kuch din baad, , woh baat phir se aayi  , Dono ne milne ka socha. , Ladki ne pehle hi decide kar liya tha — , “Jo bhi ho, main ‘haan’ nahi bolungi.” , Par kehte hain na, , jo socha jata hai, zaroori nahi woh hi ho. , Yahan bhi kuch aisa hi hua… , Irada kuch aur tha, ho kuch aur gaya — , Jab ladki usse mili, , usne ‘haan’ bol diya. ",
              },
              {
                title: "First Feelings",
                text: "The moment when hearts started beating differently…",
              },
              {
                title: "Unforgettable Memories",
                text: "Your sweetest days, late-night talks, surprises…",
              },
              {
                title: "Our Forever Plans",
                text: "Dreams, future, and the love you want to build together…",
              },
            ].map((item, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, x: -40 }}
                whileInView={{ opacity: 1, x: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.8 }}
                className="ml-10 bg-rose-50 p-6 rounded-2xl shadow-xl border border-rose-200 hover:scale-[1.02] transition-transform duration-300"
              >
                <h3 className="text-3xl font-semibold text-rose-700">{item.title}</h3>
                <p className="textg mt-2">{item.text}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Gallery Section */}
      <section className="py-20 px-6 bg-gradient-to-b from-pink-50 to-white relative z-10">
        <div className="max-w-5xl mx-auto">
          <motion.h2
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 1 }}
            className="text-4xl font-bold text-center text-rose-700 mb-12"
          >
            Our Memories
          </motion.h2>

          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {[1,2,3,4,5,6].map((n) => (
              <motion.div
                key={n}
                whileHover={{ scale: 1.03, y: -6 }}
                transition={{ type: "spring", stiffness: 300 }}
                className="rounded-xl overflow-hidden shadow-lg bg-white"
              >
                <div className="h-48 bg-gray-200 flex items-center justify-center">
                  <span className="text-gray-400">Photo {n}</span>
                </div>
                <div className="p-4">
                  <h4 className="font-semibold">Memory {n}</h4>
                  <p className="text-sm mt-1 text-gray-600">A short description of this moment.</p>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Proposal / Surprise Section */}
      <section className="py-28 bg-gradient-to-b from-rose-300 to-pink-200 text-center px-6 relative z-10">
        <motion.h2
          initial={{ opacity: 0, scale: 0.8 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1 }}
          className="text-5xl font-bold text-rose-800 mb-6 drop-shadow-lg"
        >
          Forever & Always
        </motion.h2>

        <motion.p
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 1, delay: 0.3 }}
          className="text-2xl max-w-2xl mx-auto"
        >
          No matter where life takes us, our story will always be the most beautiful part of my life.
        </motion.p>

        <motion.div
          initial={{ scale: 0 }}
          whileInView={{ scale: 1 }}
          transition={{ duration: 1, delay: 0.5 }}
          className="text-6xl mt-10"
        >
          ❤️
        </motion.div>

        <motion.button
          className="mt-10 inline-block bg-red-600 text-white px-8 py-3 rounded-full text-lg shadow-xl hover:scale-105 transition-transform"
          whileHover={{ scale: 1.03 }}
          onClick={() => {
            const el = document.getElementById("proposal-modal");
            if (el) el.classList.remove("hidden");
          }}
        >
          A Special Message
        </motion.button>

        {/* Modal */}
        <div id="proposal-modal" className="fixed inset-0 bg-black/50 hidden items-center justify-center p-6">
          <div className="bg-white rounded-2xl p-8 max-w-xl mx-auto text-center shadow-2xl">
            <h3 className="text-2xl font-bold text-rose-700">My Love</h3>
            <p className="mt-4 text-gray-700">You are my forever. Will you keep writing this story with me?</p>
            <div className="mt-6 flex justify-center gap-4">
              <button className="px-6 py-2 rounded-full bg-rose-600 text-white" onClick={() => {
                const el = document.getElementById("proposal-modal");
                if (el) el.classList.add("hidden");
              }}>Close</button>
            </div>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="py-10 text-center text-sm text-gray-600">
        Made with ❤️ — Customize this site and add your photos & music.
      </footer>
    </div>
  );
}
