import { motion } from "framer-motion";

export default function OurTimeline() {
  const story = [
    {
      title: "‚ú® Kahani Do Dilon Ki ‚ú®",
      text: `Kahani do dilo ki‚Ä¶ 
      Aaj date hai 22 November 2025.
      Kuch saal pehle do bilkul ajnabi log mile the‚Ä¶
      Pehle baatein bilkul normal thi, phir dheere-dheere dosti hui‚Ä¶
      Ek din achanak ladki ko ek message aaya ‚Äî 
      ‚ÄúI love you.‚Äù
      Ye ladki ke liye sochne wali baat thi‚Ä¶
      Jo ladka apni Instagram ID nahi dena chahata tha, 
      wo aaj itni badi baat kaise bol raha tha?
      Isliye usne use mazaak samajh kar ignore kar diya.
      Lekin kuch din baad‚Ä¶
      woh baat phir se aayi.
      Dono ne milne ka socha.
      Ladki ne pehle hi decide kar liya tha ‚Äî
      ‚ÄúJo bhi ho, main ‚Äòhaan‚Äô nahi bolungi.‚Äù
      Par kehte hain na,
      jo socha jata hai zaroori nahi woh hi ho.
      Yahan bhi kuch aisa hi hua‚Ä¶
      Irada kuch aur tha, ho kuch aur gaya ‚Äî
      Jab ladki usse mili,
      usne ‚Äòhaan‚Äô bol diya.`
    },
    {
      title: "‚ù§Ô∏è First Feelings",
      text: "Wahi pal jahan dono ke dil ek saath dhadakne lage‚Ä¶"
    },
    {
      title: "üíû Most Beautiful Memories",
      text: "Late-night talks, cute fights, caring, surprises‚Ä¶ sab ek kahani ban gaye."
    },
    {
      title: "üíç Our Forever Plans",
      text: "Ek sundar future, ek pyaara sa ghar, aur ek dusre ka saath‚Ä¶ forever."
    }
  ];

  return (
    <div className="py-24 px-6 bg-white">
      <h2 className="text-5xl font-bold text-center text-rose-700 mb-16">
        Our Timeline
      </h2>

      <div className="relative border-l-4 border-rose-400 ml-6 space-y-16 max-w-4xl mx-auto">
        {story.map((item, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, x: -40 }}
            whileInView={{ opacity: 1, x: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
            className="ml-10 bg-rose-50 p-6 rounded-2xl shadow-xl border border-rose-200 hover:scale-[1.02] transition-transform"
          >
            <h3 className="text-3xl font-semibold text-rose-700">
              {item.title}
            </h3>
            <p className="text-lg mt-2 whitespace-pre-line">{item.text}</p>
          </motion.div>
        ))}
      </div>
    </div>
  );
}
